def flip_binary(binary_string):
    # Create a translation table to swap 0s and 1s
    translation_table = str.maketrans("01", "10")
    
    # Use the translate method to swap the characters
    flipped_string = binary_string.translate(translation_table)
    
    return flipped_string

# Example usage:
binary_string = ""
flipped = flip_binary(binary_string)
print(flipped)
